SELECT a.id_ id,a.type_ type,a.key_ 'key',a.name_ 'name',a.desc_ 'desc',a.group_id_ groupId,a.group_name_ groupName,
a.bo_key_ boKey,a.bo_name_ boName,a.create_time_ createTime,a.create_by_ createBy,a.update_time_ updateTime,a.update_by_ updateBy,
a.version_ version,a.delete_ 'delete',b.persistence_type_ persistenceType FROM form_def a inner join bus_object b on a.bo_key_ = b.key_


        ${whereSql}



    ORDER BY ${orderBySql}


    ORDER BY a.id_ DESC

SELECT a.id_ "id",a.type_ "type",a.key_ "key",a.name_ "name",a.desc_ "desc",a.group_id_ "groupId",a.group_name_ "groupName",
a.bo_key_ "boKey",a.bo_name_ "boName",a.create_time_ "createTime",a.create_by_ "createBy",a.update_time_ "updateTime",a.update_by_ "updateBy",
a.version_ "version",a.delete_ "delete",b.persistence_type_ "persistenceType" FROM form_def a inner join bus_object b on a.bo_key_ = b.key_


        ${whereSql}



    ORDER BY ${orderBySql}


    ORDER BY a.id_ DESC
